const initialState = {
  name: null,
  id: null,
  token: null,
  isLoggedIn: false
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_USER":
      return { ...state,
        name: action.payload.name,
        id: action.payload.id,
        token: action.payload.token,
        isLoggedIn: true
      };

    default:
      break;
  }

  return state;
};

export default userReducer;

