import axios from 'axios'
import React, { useState, useRef } from 'react'

export const PropertiesImages = () => {
    const [images, setImages] = useState();

    const inputRef = useRef()
    const upload = (e) => {
        e.preventDefault();
        const data = new FormData() 
        data.append('images', images)

        axios.post("http://localhost:8000/api/upload", data)
        .then((response) => {
            alert(JSON.stringify(response))
            if (response.status === 200) {
            this.setState({
                responseMsg: {
                status: response.data.status,
                message: response.data.message,
                },
            });
            setTimeout(() => {
                this.setState({
                image: "",
                responseMsg: "",
                });
            }, 2000);

            document.querySelector("#imageForm").reset();
            // getting uploaded images
            this.refs.child.getImages();
            }
        })
        .catch((error) => {
            console.error(error);
        });
    }

    return (
        <div>
            <div>
                {/* <input type="file" value={images} placeholder="image upload" label="upload"
                onChange={(e)=>setImages(e.target.value)} */}
                <input 
                type="file" id="input"
                onChange={() => setImages(inputRef.current.files[0])}
                ref={inputRef}
            />

            <button type="submit" onClick={upload}>upoad</button>
            </div>
        </div>
    )
            }
