import React from 'react';
import { useState } from 'react';
import { useHistory } from 'react-router-dom';
import Background from '../../images/bg_1.jpeg';
import Spinner from 'react-bootstrap/Spinner'
import AuthService from "api/AuthService";


function Login() {
  const [email, setEmail] = useState();
  const [checkemail,setCheckmail]=useState();
  const [checkpassword,setCheckpassword]=useState();
  const [password, setPassword] = useState();
  const [message, setMessage] = useState("");
  const [isloading, setLoading] = useState(false);
  const history = useHistory();


  const loginValidation = ()=>{
    if(!email ||  email.length <=10){
      setCheckmail('please fill the correct email')
      return false;
    }else if(!password || password.length<8)
    {
      setCheckpassword('password should be 8 character')
      return false;
    }
        return true;
      }


  async function submit(e) {

    if(loginValidation()){

      // Prevent Refresh
      e.preventDefault();

      // make data for login
      const data = { email: email, password: password };

      setLoading(true);

    // API call for login
    const response = await AuthService.login(data);

    console.log(response);

    if (response.success == false) {
      setMessage(response.error);
      setLoading(false);
    }
    else {
      setEmail('');
      setPassword('');

      // if sucessfully response from login API then move to next Screen
      history.push({
        pathname: '/propertypost',
        state: { token: response.data }
      });
      setLoading(false);
    }

    }

  }

  return (
    <div class="d-md-flex p-half">
      <div class="p-bg" style={{ backgroundImage: "url(" + Background + ")" }}></div>
      <div class="p-contents">
        <div class="p-containers">
          <div class="row align-items-center justify-content-center">
            <div class="col-md-12">
              <div class="form-block mx-auto">
                <div class="p-mb-12 ">
                  Login
                </div>

                <form onSubmit={submit}>
                  <div class="p-field">
                    <div class="p-form-group first">
                      <input type="text" class="p-form-control" placeholder="Enter Email ID or Mobile No."
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}

                      />
                      {  (!email ||  email.length <=10) &&  <p>{checkemail}</p>}
                    </div>

                    <div class="p-form-group first">
                      <input type="password" class="p-form-control" placeholder="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                      />
                      { (!password || password.length<8) &&   <p>{checkpassword}</p>}
                    </div>


                    <div class="p-form-group last mb-3">
                      <input type="submit" value="login" class="btn btn-block btn-danger" />
                    </div>
                    <div>
                      {isloading && <Spinner animation="border" variant="primary" />}
                    </div>
                    <div class="p-help">
                      <a href="#">Need Help?</a>
                    </div>
                  </div>

                  <div class="p-login">
                    <div class="p-logintext">Or login using</div>
                  </div>

                  <div class="login-with-social">
                    <div class="text-center p-py-3 p-connections"> <a href="https://wwww.facebook.com" target="_blank" class="px-2"> <img src="https://www.dpreview.com/files/p/articles/4698742202/facebook.jpeg" alt="" />&nbspFacebook  </a> | <a href="https://www.google.com" target="_blank" class="px-2"> <img src="https://www.freepnglogos.com/uploads/google-logo-png/google-logo-png-suite-everything-you-need-know-about-google-newest-0.png" alt="" />&nbspGoogle </a></div>
                  </div>
                  <div class="p-back">
                    <div class="p-signup">
                      New to Magicbricks? <a class="p-loginbtn1" href="#">Sign Up</a>
                    </div>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Login
