import axios from 'axios'
import React, { useState, useRef, useEffect } from 'react'

export const PropertiesImages = ({images,setImages})=> {
    const [preview, setPreview] = useState()
    const[allimages,setAllimages]=useState([])  


    const selected_images={images:images,preview:preview}


    useEffect(() => {
        if (!images || images.length==0) {
            setPreview(undefined)
            return
        }

        const objectUrl = URL.createObjectURL(images)
        setPreview(objectUrl)

        return () => URL.revokeObjectURL(objectUrl)
    }, [images])







    const inputRef = useRef()

    const upload = (e) => {
        e.preventDefault();
        const data = new FormData() 
        data.append('images', images)

        axios.post("http://localhost:8000/api/upload", data)
        .then((response) => {
            alert(JSON.stringify(response.data))
            if (response.status === 200) {
            this.setState({
                responseMsg: {
                status: response.data.status,
                message: response.data.message,
                },
            });
            setTimeout(() => {
                this.setState({
                image: "",
                responseMsg: "",
                });
            }, 2000);

            document.querySelector("#imageForm").reset();
            // getting uploaded images
            this.refs.child.getImages();
            }
        })
        .catch((error) => {
            console.error(error);
        });
    }

    return (
        <div>
            <div>

                <input 
                 type="file" id="input"
                onChange={() => setImages(...inputRef.current.files)}
                ref={inputRef}
                multiple="multiple"/>
               <div >
                {images &&  <img src={preview}  style = {{height:"100px"}} 

                /> }
                </div>
            <button type="submit" onClick={upload} style={{display:"none"}}>upoad</button>
            </div>
        </div>
    )
            }
