import React from 'react';
import { useState,useEffect } from 'react';
import {Form} from 'react-bootstrap';
import {Button} from 'react-bootstrap';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import Background from '../../images/bg_1.jpeg';
import Spinner from 'react-bootstrap/Spinner'
import properties from '../../properties'


function Login(){
  const [email, setEmail] = useState();
  const [checkemail,setCheckmail]=useState();
  const [password, setPassword] = useState();
  const [checkpassword,setCheckpassword]=useState();
  const [allentry,setAllentry] = useState([]);
  const [message, setMessage] = useState("");
  const[isloading,setLoading] = useState(false);
  const history = useHistory();







   const push = (plaintextToken)=>{
    history.push({
      pathname: '/propertypost',
      state: { token: plaintextToken}
  });   }

   const submit = (e)=>{
    e.preventDefault();
       const newEntry = {email:email,password:password};
       setAllentry([...allentry,newEntry]);
       login(newEntry);
   }

   const loginValidation = ()=>{
        if(!email || email.lenghth>10){
          setCheckmail('please fill the email')
          return false;
        }else if(!password || password<8)
        {
          setCheckpassword('password should be 9 character')
          return false;
        }
            return true
          }


  async function login(newEntry) {

    if(loginValidation()){

      setLoading(true);

    const rest = await axios.post(properties.login_url, newEntry);
    console.log(rest);
    if (rest.data.success == false) {
      setMessage(rest.data.error);
      setLoading(false);
    }
    else {
      setEmail('');
      setPassword('');
      push(rest.data.data);
      setLoading(false);
    }





    }

  }



    return (


    <div class="d-md-flex p-half">
      <div class="p-bg"style={{ backgroundImage: "url(" + Background + ")" }}></div>
      <div class="p-contents">


        <div class="p-containers">
          <div class="row align-items-center justify-content-center">
            <div class="col-md-12">
              <div class="form-block mx-auto">
                <div class="p-mb-12 ">
                Login
                </div>

                <form onSubmit={submit}>



                  <div class="p-field">
                    <div class="p-form-group first">
                    <input type="text" class="p-form-control" placeholder="Enter Email ID." 
                    value={email}
                    onChange={(e)=>setEmail(e.target.value)}

                    />
                    <p>{checkemail}</p>
                  </div>
                 <div> 
                 {isloading && <Spinner animation="border" variant="primary" />}
                 </div>

                  <div class="p-form-group first">
                    <input type="password" class="p-form-control" placeholder="password" 
                    value={password}
                    onChange={(e)=>setPassword(e.target.value)}


                    />
                    <p>{checkpassword}</p>
                  </div>


                  <div class="p-form-group last mb-3">
                    <input type="submit" value="login" class="btn btn-block btn-danger" />
                  </div>
                  <div class="p-help">
                    <a href="#">Need Help?</a>
                  </div>
                </div>

                <div class="p-login">
                  <div class="p-logintext">Or login using</div>
                </div>

                <div class="login-with-social">
                  <div class="text-center p-py-3 p-connections"> <a href="https://wwww.facebook.com" target="_blank" class="px-2"> <img src="https://www.dpreview.com/files/p/articles/4698742202/facebook.jpeg" alt="" />&nbspFacebook  </a> | <a href="https://www.google.com" target="_blank" class="px-2"> <img src="https://www.freepnglogos.com/uploads/google-logo-png/google-logo-png-suite-everything-you-need-know-about-google-newest-0.png" alt=""/>&nbspGoogle </a></div>
                </div>
                <div class="p-back">
                  <div class="p-signup">  
                    New to Magicbricks? <a class="p-loginbtn1" href="#">Sign Up</a>
                  </div>
                  </div>

                </form>

              </div>

            </div>

          </div>

        </div>

      </div>


    </div>



    )
}

export default Login
